import React, {useState} from 'react';

import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';
import ExpenseFilter from '../NewExpanse/ExpenseFilter';

const Expenses = (props) => {
  const [selectYear,setSelectYear]= useState('2021')
  const ChangeFilter=SelectedYear=>{
    setSelectYear(SelectedYear);
  }
  
  return (
    <Card className="expenses">
      <ExpenseFilter  selected={selectYear} ClickThis={ChangeFilter} />
      {props.items.map(exp=><ExpenseItem title={exp.title} amount={exp.amount} date={exp.date}/>)}
      <ExpenseItem
        title={props.items[0].title}
        amount={props.items[0].amount}
        date={props.items[0].date}
      />
      <ExpenseItem
        title={props.items[1].title}
        amount={props.items[1].amount}
        date={props.items[1].date}
      />
      <ExpenseItem
        title={props.items[2].title}
        amount={props.items[2].amount}
        date={props.items[2].date}
      />
      <ExpenseItem
        title={props.items[3].title}
        amount={props.items[3].amount}
        date={props.items[3].date}
      />
    </Card>
  );
}

export default Expenses;
